.container {
	position: absolute;
	top: 0px;
	bottom: 0px;
	width: 100%;

	.top_container {
		width: 100%;
		height: 30px;
		background: #f3f3f3;

		.top {
			width: 1200px;
			margin: 0 auto;
			height: 30px;

			.top_left {
				float: left;
				height: 30px;
				line-height: 30px;
				display: inline;
				margin: 0 0 0 20px;
			}

			.top_right {
				float: right;
				height: 30px;
				line-height: 30px;
				display: inline;
				margin: 0 20px 0 0;
				color: red;
			}
		}
	}

	.search {
		width: 100%;
		border-bottom: 5px solid #098a30;

		.search_total {
			width: 1200px;
			margin: 0 auto;
			height: 120px;
			padding: 10px 0 0 0;

			.search_left {
				float: left;
				margin-right: 50px;

				.logo {
					margin-right: 50px;
					float: left;

					.logo_image {
						width: 218px;
						height: 95px;
					}
				}

				.find {
					float: right;
					margin-top: 30px;

					.search_input {
						width: 265px;
					}
				}
			}

			.search_right {
				float: right;

				.navigation {
					width: auto;
					height: 100%;
					margin-top: 20px;
					font-size: 20px;

					.navigation_menu_item {
						font-size: 16px;
					}

					.navigation_menu_item.is-active {
						color: #409eff;
						font-weight: bold;
						font-size: 20px;
					}
				}
			}
		}
	}

	.exhibition {
		width: 100%;
		height: 225px;

		.rotation {
			margin: 0 auto;
			padding: 10px 0 15px 0;
			width: 1200px;
			height: 200px;

			.el-carousel__item:nth-child(2n) {
				background-color: #99a9bf;
			}

			.el-carousel__item:nth-child(2n + 1) {
				background-color: #d3dce6;
			}
		}
	}

	.show_main {
		width: 100%;
		height: 600px;

		.show_main_container {
			width: 1198px;
			height: 598px;
			margin: 10px auto;

			.el-aside {
				border: 1px solid #67c23a;

				.elaside_total {
					padding: 15px 15px 0;
					background-color: #fcfcfc;

					.sort_title {
						overflow: hidden;
						text-align: center;

						.span_1 {
							font-size: 12px;
							color: #999;
							font-family: arial, \5fae\8f6f\96c5\9ed1, \5b8b\4f53;
						}
						.span_2 {
							position: relative;
							font-size: 22px;
							height: 30px;
							line-height: 30px;
							color: #000;
							padding: 0 20px 15px;
							border-bottom: 1px solid #e1e1e1;
						}
					}

					.elmenu_total {
						border: none;

						.elmenu_submenu {
							border-bottom: 1px solid #e1e1e1;

							
						}
					}
				}
			}
			.elaside_1 {
				border: none;
			}

			.show_main_container_children {
				border: 1px solid #eee;
				width: 900px;

				.el-header {
					background-color: #fcfcfc;

					.elheader_left {
						float: left;
						width: 156px;
						padding: 21px 0;

						.elheader_span {
							font-size: 16px;
							line-height: 1;
							color: red;
						}
					}

					.elheader_right {
						float: right;
						padding: 22px 0;
						width: 700px;
					}
				}
			}
		}
	}
}
