.container {
	position: absolute;
	top: 0px;
	bottom: 0px;
	width: 100%;

	.top_container {
		width: 100%;
		height: 30px;
		background: #f3f3f3;

		.top {
			width: 1200px;
			margin: 0 auto;
			height: 30px;

			.top_left {
				float: left;
				height: 30px;
				line-height: 30px;
				display: inline;
				margin: 0 0 0 20px;
			}

			.top_right {
				float: right;
				height: 30px;
				line-height: 30px;
				display: inline;
				margin: 0 20px 0 0;
				color: red;
			}
		}
	}

	.search {
		width: 100%;
		border-bottom: 5px solid #098A30;
		
		.search_total {
			width: 1200px;
			margin: 0 auto;
			height: 120px;
			padding: 10px 0 0 0;
			
			.search_left {
				float: left;
				margin-right: 50px;

				.logo {
					margin-right: 50px;
					float: left;

					.logo_image {
						width: 218px;
						height: 95px;
					}
				}

				.find {
					float: right;
					margin-top: 30px;

					.search_input {
						width: 265px;
					}
				}
			}

			.search_right {
				float: right;

				.navigation {
					width: auto;
					height: 100%;
					margin-top: 20px;
					font-size: 20px;

					.navigation_menu_item {
						font-size: 16px;
					}

					.navigation_menu_item.is-active {
						color: #409eff;
						font-weight: bold;
						font-size: 20px;
					}
				}
			}
		}
	}

	.exhibition {
		width: 1200px;
		margin: 0 auto;
		height: 453px;
		padding: 10px 0 0 0;

		.sort {
			float: left;
			width: 250px;
			height: 450px;
			border: 1px solid #67c23a;
		}

		.rotation {
			float: right;
			width: 900px;
			height: 450px;

			.el-carousel__item h3 {
				color: #475669;
				font-size: 14px;
				opacity: 0.75;
				line-height: 150px;
				margin: 0;
			}

			.el-carousel__item:nth-child(2n) {
				background-color: #99a9bf;
			}

			.el-carousel__item:nth-child(2n + 1) {
				background-color: #d3dce6;
			}
		}
	}

	.photo_show {
		width: 1200px;
		height: 238px;
		margin: 0 auto;

		.show_image {
			padding: 25px 0 35px 0;
			width: 100%;
			height: 178px;
		}
	}

	.divider_1 {
		width: 1200px;
		margin: 0 auto;

		.divider_content_1 {
			margin: 28px 0;
			.span_1 {
				font-size: 26px;
				color: #429b49;
				display: block;

				.span_2 {
					font-size: 20px;
					color: #222;
				}
			}
		}
	}

	.fruit_display_1 {
		width: 1200px;
		height: 560px;
		margin: 0 auto;

		.container_2 {
			padding: 20px 0 0 0;
			height: 540px;

			.picture_1 {
				width: 275px;
				height: 540px;
				float: left;

				.img_picture_1 {
					width: 100%;
					height: 490px;
				}
			}

			.fruit_card_1 {
				float: right;
				width: 900px;
				height: 540px;

				.el-col-6 {
					width: 28%;

					.fruit_card {
						.fruit_image {
							width: 100%;
							height: 181px;
							display: block;
						}

						.time {
							font-size: 12px;
							color: #999;
						}

						.bottom {
							margin-top: 10px;
							line-height: 9px;
						}

						.button {
							padding: 0;
							float: right;
						}

						.clearfix:before,
						.clearfix:after {
							display: table;
							content: '';
						}

						.clearfix:after {
							clear: both;
						}
					}
				}
			}
		}
	}
}
